public class ElseIfLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String city="Bengaluru";
		if (city=="Meeruth"){
			System.out.println("The city is "+ city);
		}
		else if(city=="Delhi") {
			System.out.println("The city is "+ city);
		}
		else if(city=="Yadgiri") {
			System.out.println("The city is "+ city);
		}
		else {
			System.out.println("The city is "+ city);
		}
		
}
}